import React, { useState } from 'react';

const SearchBar = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    onSearch(searchTerm);
  };

  return (
    <div className="container d-block float-end">
    <div className="container-fluid w-25 d-block">
    <form onSubmit={handleSearchSubmit} className='form'>
      <div className="input-group">
        <div className="form-floating w-25">
          <input className='form-control' id='search' type='text' value={searchTerm} onChange={handleSearchChange} placeholder='' onKeyUp={handleSearchSubmit}/>
          <label htmlFor="#search">Search name/location</label>
        </div>
        <span class="input-group-text" id="basic-addon2">üîç</span>
      </div>
        {/* <button type='submit'>Search</button> */}
    </form>
    </div>
    </div>
  );
};

export default SearchBar;
