import React, { useState } from 'react';

const SearchBar = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    onSearch(searchTerm);
  };

  return (
    <div className="container-fluid">
    <form onSubmit={handleSearchSubmit} className='form row'>
      <label htmlFor="#search">search name/location</label>
      <input className='form-control col-sm-6' id='search' type='text' value={searchTerm} onChange={handleSearchChange} placeholder='' />
      <button type='submit'>Search</button>
    </form>
    </div>
  );
};

export default SearchBar;
