import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Table from './components/Table';
import Pagination from './components/Pagination';
import SearchBar from './components/SearchBar';
import SortOptions from './components/SortOptions';

const App = () => {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [customersPerPage] = useState(20);

  useEffect(() => {
    const fetchCustomers = async () => {
      setLoading(true);
      const res = await axios.get(`http://localhost:5000/api/customers?page=${currentPage}&limit=${customersPerPage}`);
      setCustomers(res.data);
      setLoading(false);
    };

    fetchCustomers();
  }, [currentPage, customersPerPage, resCustomers]); 

  const searchCustomers = async (term) => {
    setLoading(true);
    const res = await axios.get(`http://localhost:5000/api/customers/search?term=${term}`);
    setCustomers(res.data);
    setLoading(false);
  };

  const sortCustomers = async (sortBy) => {
    setLoading(true);
    const res = await axios.get(`http://localhost:5000/api/customers/sort?by=${sortBy}`);
    setCustomers(res.data);
    setResCustomers(res.data);
    setLoading(false);
  };

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className='container'>
      <h1>Customer Data</h1>
      <SearchBar searchCustomers={searchCustomers} />
      <SortOptions sortCustomers={sortCustomers}/>
      <Table customers={customers} loading={loading} />
      <Pagination
        customersPerPage={customersPerPage}
        totalCustomers={customers.length}
        paginate={paginate}
      />
    </div>
  );
};

export default App;
