import React, { useState } from 'react';

const SearchBar = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    onSearch(searchTerm);
  };

  const searchStyle = {
    'float' : 'right',
    'margin-right': '150px',
     'margin-bottom' : '10px'
  }

  const inputStyle = {
    'height' : '10px'
  }

  return (
    <div className="container w-25" style={searchStyle}>
    <form onSubmit={handleSearchSubmit} className='form'>
      <div className="input-group input-group-sm">
        <div className="form-floating w-25">
          <input className='form-control' id='search' type='text' value={searchTerm} onChange={handleSearchChange} placeholder='' onKeyUp={handleSearchSubmit} style={inputStyle}/>
          <label htmlFor="#search">Search name/location</label>
        </div>
        <span class="input-group-text" id="search-icon">üîç</span>
      </div>
        {/* <button type='submit'>Search</button> */}
    </form>
    </div>
  );
};

export default SearchBar;
