import React from 'react';

const Pagination = ({ currentPage, totalCustomers, customersPerPage, onPageChange }) => {
  const totalPages = Math.ceil(totalCustomers / customersPerPage);

  const handleClick = (pageNumber) => {
    if (pageNumber >= 1 && pageNumber <= totalPages) {
      onPageChange(pageNumber);
    }
  };

  return (
    <div className='text-center w-50'>
    <div className="mb-5 mt-4">
      <button className={`btn ${currentPage == 1 ? 'btn-danger  ' : 'btn'}`} onClick={() => handleClick(currentPage - 1)} disabled={currentPage == 1}>⏮️</button>
      {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
        <button className={`btn ${page === currentPage ? 'btn-primary active' : 'btn-secondary'}`} key={page} onClick={() => handleClick(page)}>
          {page}
        </button>
      ))}
      <button className='btn' onClick={() => handleClick(currentPage + 1)} disabled={currentPage == totalPages}>⏭️</button>
      </div>
    </div>
  );
};

export default Pagination;
