import React from 'react';

const Pagination = ({ currentPage, totalCustomers, customersPerPage, onPageChange }) => {
  const totalPages = Math.ceil(totalCustomers / customersPerPage);

  const handleClick = (pageNumber) => {
    if (pageNumber >= 1 && pageNumber <= totalPages) {
      onPageChange(pageNumber);
    }
  };

  return (
    <div className="d-flex justify-content-center mb-5 mt-4">
      <button onClick={() => handleClick(currentPage - 1)} disabled={currentPage == 1}>⏮️</button>
      {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
        <button key={page} onClick={() => handleClick(page)}>
          {page}
        </button>
      ))}
      <button onClick={() => handleClick(currentPage + 1)} disabled={currentPage == cus}>⏭️</button>
    </div>
  );
};

export default Pagination;
