import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Table from './components/Table';
import Pagination from './components/Pagination';
import SearchBar from './components/SearchBar';
import SortOptions from './components/SortOptions';

const App = () => {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [customersPerPage] = useState(20);
  const [sortBy, setSortBy] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchCustomers = async () => {
      setLoading(true);
      const res = await axios.get(`http:/api/customers?page=${currentPage}&limit=${customersPerPage}&sortBy=${sortBy}&searchTerm=${searchTerm}`);
      setCustomers(res.data);
      setLoading(false);
    };

    fetchCustomers();
  }, [currentPage, customersPerPage, sortBy, searchTerm]);

  const handleSearch = (term) => {
    setSearchTerm(term);
    setCurrentPage(1);
  };

  const handleSort = (sortOption) => {
    setSortBy(sortOption);
    setCurrentPage(1);
  };

  const handlePagination = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className='container'>
      <h1>Customer Data</h1>
      <SearchBar onSearch={handleSearch} />
      <SortOptions onSort={handleSort} />
      <Table customers={customers} loading={loading} />
      <Pagination
        currentPage={currentPage}
        totalPages={Math.ceil(customers.length / customersPerPage)}
        onPageChange={handlePagination}
      />
    </div>
  );
};

export default App;
